document.addEventListener("DOMContentLoaded",function(){if(window.innerWidth<=768){new Swiper(".swiper-container",{effect:"cards",grabCursor:!0,centeredSlides:!0,slidesPerView:"auto",loop:!0,cardsEffect:{slideShadows:!1},pagination:{el:".swiper-pagination",clickable:!0},navigation:{nextEl:".swiper-button-next",prevEl:".swiper-button-prev"}});document.querySelectorAll(".swiper-slide").forEach(function(e){e.addEventListener("click",function(){e.classList.toggle("active")})})}});